// --- Конфигурация Роутера ---
const FMP_BASE_URL = 'https://financialmodelingprep.com';

const routingTable = {
  'Insider.Trading.Latest': { path: '/stable/insider-trading/latest', paramIn: 'query' },
  'Insider.Trading.ReportingName': { path: '/stable/insider-trading/reporting-name', paramIn: 'query' },
  'Insider.Trading.Search': { path: '/stable/insider-trading/search', paramIn: 'query' },
  'Company.Profile': { path: '/stable/profile', paramIn: 'query' },
};


// --- Логика Роутера ---

// ВАЖНО: API ключ настраивается через n8n Credentials
// Создайте credential типа "Generic Credential" с именем "FMP API Key"
// В поле Value укажите ваш FMP API ключ
const apiKey = $credentials.fmpApiKey;

if (!apiKey) {
  throw new Error('FMP API Key не настроен. Создайте credential "FMP API Key" в n8n.');
}

// Получаем данные напрямую от агента (из узла Start)  
const toolName = $json.toolName;
const params = $json.params || {};

// Добавляем API ключ к параметрам запроса
params.apikey = apiKey;

const route = routingTable[toolName];
if (!route) {
  throw new Error(`Ошибка: Команда "${toolName}" не найдена в таблице маршрутизации.`);
}

let finalUrl = FMP_BASE_URL + route.path;

if (route.paramIn === 'query' && Object.keys(params).length > 0) {
  const queryParts = [];
  for (const k in params) {
    if (params[k] !== undefined && params[k] !== null) {
      queryParts.push(encodeURIComponent(k) + '=' + encodeURIComponent(params[k]));
    }
  }
  if (queryParts.length > 0) {
    finalUrl += '?' + queryParts.join('&');
  }
} else if (route.paramIn === 'path') {
  for (const k in params) {
    if (route.path.includes(`\${${k}}`)) {
      finalUrl = finalUrl.replace(`\${${k}}`, params[k]);
    }
  }
}

const output = {
  url: finalUrl,
};

return output;
